import React from 'react'
import { motion } from 'framer-motion'
import { AiOutlineArrowRight } from 'react-icons/ai'

import './Searchbar.scss'

const Searchbar = () => {

    const [isHover, toggleHover] = React.useState(false);
    const toggleHoverMenu = () => {
      toggleHover(!isHover);
    };
    const [isMouse, toggleMouse] = React.useState(false);
    const toggleMouseMenu = () => {
      toggleMouse(!isMouse);
    };

    const subMenuAnimate = {
        enter: {
          opacity: 1,
          rotateX: 0,
          transition: {
            duration: 0.5
          },
          display: "block"
        },
        exit: {
          opacity: 0,
          rotateX: -15,
          transition: {
            duration: 0.5,
            delay: 0.3
          },
          transitionEnd: {
            display: "none"
          }
        }
      };

  return (
    <div className='form-wrapper'>
        <h2 className='form-title'>Search a summonner</h2>
        <form className='form'>
            <div className="flex-item">
            <h1>onMouse</h1>
            <motion.div
                className="menu-item"
                onMouseEnter={toggleMouseMenu}
                onMouseLeave={toggleMouseMenu}
            >
                <a href="/">Menu Item</a>
                <motion.div
                className="sub-menu"
                initial="exit"
                animate={isMouse ? "enter" : "exit"}
                variants={subMenuAnimate}
                >
                <div className="sub-menu-background" />
                <div className="sub-menu-container">
                    <div className="sub-menu-item">Submenu Item 1</div>
                    <div className="sub-menu-item">Submenu Item 2</div>
                    <div className="sub-menu-item">Submenu Item 3</div>
                    <div className="sub-menu-item">Submenu Item 4</div>
                    <div className="sub-menu-item">Submenu Item 5</div>
                </div>
                </motion.div>
            </motion.div>
            </div>{" "}
            <label className='summoner-label'>Summoners name:</label>
            <input className='summoner-input' type='text' placeholder='Ex: TheFaithfulDevil' />
            <button className='search-button'>
                <div className='arrow-icon'>
                    <AiOutlineArrowRight />
                </div>
            </button>
        </form>
    </div>
  )
}

export default Searchbar
