import React, { useState } from 'react'
import { AiOutlineArrowRight } from 'react-icons/ai'
import axios from 'axios'

import './Searchbar.scss'



const API_KEY = 'RGAPI-bf44f153-491b-482e-adde-af9efdb8eb49'

const Searchbar = () => {

  const [searchText, setSearchText] = useState('')
  const [server, setServer] = useState('eun1')

  const searchForPlayer = (e) => {
    e.preventDefault()
    const API_CALL_STRING = 'https://eun1.api.riotgames.com/lol/summoner/v4/summoners/by-name/' 
        + searchText + '?api_key=' + API_KEY

    axios.get(API_CALL_STRING).then(() => {
            //success
        }).catch((err) => {
            //error
        }
    )    
  }

  return (
    <div className='form-wrapper'>
        <h2 className='form-title'>Search a summonner</h2>
        <form className='form'>
            <label className='summoner-label'>Summoners name:</label>
            <input 
                className='summoner-input' 
                type='text' 
                placeholder='Ex: TheFaithfulDevil' 
                onChange={e => setSearchText(e.target.value)}
            />
            <button 
                className='search-button'
                onClick={e => searchForPlayer(e)}
            >
                <div className='arrow-icon'>
                    <AiOutlineArrowRight />
                </div>
            </button>
        </form>
    </div>
  )
}

export default Searchbar
