import React, { useState } from 'react'
import axios from 'axios'

import './App.scss';
import Searchbar from './components/Searchbar/Searchbar';
import ResultsBox from './components/ResultsBox/ResultsBox';


const API_KEY = 'RGAPI-bf44f153-491b-482e-adde-af9efdb8eb49'


function App() {

  const [searchText, setSearchText] = useState('')
  const [server, setServer] = useState('eun1')

  const [playerData, setPlayerData] = useState({})


  const searchForPlayer = (e) => {
    e.preventDefault()
    const API_CALL_STRING = 'https://eun1.api.riotgames.com/lol/summoner/v4/summoners/by-name/' 
        + searchText + '?api_key=' + API_KEY

    axios.get(API_CALL_STRING).then((res) => {
            setPlayerData(res.data)
        }).catch((err) => {
            console.error(err);
        }
    )    
  }

  console.log(playerData)

  return (
    <div className='App'>
      <Searchbar 
        searchText={searchText}
        setSearchText={setSearchText}
        server={server}
        setServer={setServer}
        searchForPlayer={searchForPlayer}
      />
      {JSON.stringify(playerData) !== '{}' && (
        <ResultsBox playerData={playerData} />

      )}
    </div>
  );
}

export default App;
