import React, { useState } from 'react'
import axios from 'axios'



import './ResultsBox.scss'

const ResultsBox = ({ playerData, matches }) => {

  const [championIconUrl, setChampionIconUrl] = useState('')


  const getPlayerIcon = (championName) => {
    axios.get('http://ddragon.leagueoflegends.com/cdn/12.20.1/img/champion/' + championName)
      .then(res => [
        setChampionIconUrl(res.data)
      ]).catch(err => {
        console.log(err);
      })
  }

  return (
    <div className='results-wrapper'>
      <div className='champion-wrapper'>
        <img 
            className='summoner-icon' 
            src={`http://ddragon.leagueoflegends.com/cdn/12.20.1/img/profileicon/${playerData.profileIconId}.png`} 
            alt='summoner-icon'
        />
        <h2 className='summoner-name'>{playerData.name}</h2>
        <p className='summoner-level'>Level: {playerData.summonerLevel}</p>
      </div>
          {matches.length !== 0 ? matches.map((match, index) => {

            const participantIndex = match.metadata.participants.indexOf(playerData.puuid)
            const participant = match.info.participants[participantIndex]
            const participantTeam = participant.teamId
            const teaminfo = match.info.teams
            const teamIndex = teaminfo.findIndex(e => e.teamId === participantTeam)
            getPlayerIcon(participant.championName)

            console.log(match)
            console.log(participantTeam)
            console.log('myTeam', teaminfo[teamIndex].win)

            return(
              <div className={`match-container ${teaminfo[teamIndex].win ? 'win' : 'lose'}`}>
                <div className='left-side'>
                  <h5 className={`game-mode ${teaminfo[teamIndex].win ? 'win' : 'lose'}`}>{match.info.gameMode} â€“ {teaminfo[teamIndex].win ? 'Victory' : 'Defeat'}</h5>
                  <p className=''>{participant.championName}</p>
                  <p>KDA: {participant.kills}/{participant.deaths}/{participant.assists}</p>
                </div>
                <div className='middle-side'>
                  <img className='champion-icon' src={championIconUrl} alt='champion-icon'/>
                </div>
                <div className='right-side'>

                </div>
              </div>

              // [{a:1, b:2},{a:3, b:4}]

            )
          }) : <h2>no info</h2>}
  </div>
  )
}

export default ResultsBox
