import React, { useEffect, useState } from 'react'
import { motion } from 'framer-motion'
import axios from 'axios'

import './App.scss';
import Searchbar from './components/Searchbar/Searchbar';
import ResultsBox from './components/ResultsBox/ResultsBox';



function App() {

  const [searchText, setSearchText] = useState('')
  const [summonerName, setSummonerName] = useState('')
  const [server, setServer] = useState('eun1')

  const [playerData, setPlayerData] = useState({})
  const [matches, setMatches] = useState([])



  // console.log(playerData);
  // console.log(matches)


  useEffect(() => {
    axios.get('http://localhost:4000/searchPlayer', { params: { username: summonerName } })
    .then(res => {
      setPlayerData(res.data)
    }).catch(err => {
      console.log(err);
    })

  axios.get('http://localhost:4000/latest20Matches', { params: { username: summonerName } })
    .then(res => {
      setMatches(res.data)
    }).catch(err => {
      console.log(err);
    })   
  }, [summonerName])

  return (
    <div className='App'>
      <Searchbar 
        searchText={searchText}
        setSearchText={setSearchText}
        summonerName={summonerName}
        setSummonerName={setSummonerName}
        server={server}
        setServer={setServer}
      />
      {
        summonerName !== '' && (
        <motion.div>
          <ResultsBox playerData={playerData} matches={matches} />
        </motion.div>

      )}
    </div>
  );
}

export default App;
