import React, { useState } from 'react'
import { motion } from 'framer-motion'
import axios from 'axios'

import './App.scss';
import Searchbar from './components/Searchbar/Searchbar';
import ResultsBox from './components/ResultsBox/ResultsBox';


const API_KEY = 'RGAPI-bf44f153-491b-482e-adde-af9efdb8eb49'




function App() {

  const [searchText, setSearchText] = useState('')
  const [server, setServer] = useState('eun1')

  const [playerData, setPlayerData] = useState({})
  const [latestGameIds, setLatestGameIds]= useState([])

  const searchForPlayer = (e) => {
    e.preventDefault()
    const API_CALL_STRING = 'https://tr1.api.riotgames.com/lol/summoner/v4/summoners/by-name/' 
        + searchText + '?api_key=' + API_KEY

    axios.get(API_CALL_STRING).then((res) => {
            setPlayerData(res.data)
        }).catch((err) => {
            console.error(err);
      }
    )
    searchForLatestGameIds()  
  }

  const searchForLatestGameIds = () => {
    const API_CALL_STRING = 'https://europe.api.riotgames.com/lol/match/v5/matches/by-puuid/' + playerData.puuid + '/ids?api_key=' + API_KEY
    axios.get(API_CALL_STRING).then((res) => {
      setLatestGameIds(res.data)
    }).catch((err) => {
      console.error(err)
    })
  }

  console.log(latestGameIds)

  return (
    <div className='App'>
      <Searchbar 
        searchText={searchText}
        setSearchText={setSearchText}
        server={server}
        setServer={setServer}
        searchForPlayer={searchForPlayer}
      />
      {JSON.stringify(playerData) !== '{}' && (
        <motion.div>
          <ResultsBox playerData={playerData} />
        </motion.div>

      )}
    </div>
  );
}

export default App;
