import React, { useState } from 'react'
import { motion } from 'framer-motion'
import axios from 'axios'

import './App.scss';
import Searchbar from './components/Searchbar/Searchbar';
import ResultsBox from './components/ResultsBox/ResultsBox';


const API_KEY = 'RGAPI-bf44f153-491b-482e-adde-af9efdb8eb49'




function App() {

  const [searchText, setSearchText] = useState('')
  const [server, setServer] = useState('eun1')

  const [playerData, setPlayerData] = useState({})
  const [latestMatchIds, setLatestMatchIds] = useState([])
  const [matches, setMatches] = useState([])


  const searchForLatestMatches = (e) => {
    e.preventDefault()
    axios.get('http://localhost:4000/latest20Matches')
      .then(res => {
        setMatches(res.data)
      }).catch(err => {
        console.log(err);
      })
  }

  // Responsible for fetching the summoner data
  const searchForPlayer = (e) => {
    e.preventDefault()
    const API_CALL_STRING = 'https://tr1.api.riotgames.com/lol/summoner/v4/summoners/by-name/' 
        + searchText + '?api_key=' + API_KEY

    axios.get(API_CALL_STRING).then((res) => {
            setPlayerData(res.data)
        }).catch((err) => {
            console.error(err);
      }
    )
    searchForLatestMatchIds()  
  }

  // Returns an array of the match ids
  const searchForLatestMatchIds = () => {
    const API_CALL_STRING = 'https://europe.api.riotgames.com/lol/match/v5/' +  + '?api_key=' + API_KEY
    axios.get(API_CALL_STRING).then((res) => {
      setLatestMatchIds(res.data)
    }).catch((err) => {
      console.error(err)
    })

    /* 
      "TR1_1347837384"
      "TR1_1347830024"
      "TR1_1347818159"
      "TR1_1345803613"
      "TR1_1345796290"
      "TR1_1345785978"
      "TR1_1342573635"
      "TR1_1342569136"
      "TR1_1341004371"
      "TR1_1336931476"
      "TR1_1336930431"
      "TR1_1336732722"
      "TR1_1336742184"
      "TR1_1336761253"
      "TR1_1336750694"
      "TR1_1336730040"
      "TR1_1336421497"
      "TR1_1336440286"
      "TR1_1336369802"
      "TR1_1336387850"
    */

      latestMatchIds.map((matchId) => {
        return getMatchDetails(matchId)
      })
  }

  // Returns an a huge data object of a specific match
  const getMatchDetails = (matchId) => {
    const API_CALL_STRING = 'https://europe.api.riotgames.com/lol/match/v5/matches/' + matchId + '?api_key=' + API_KEY

    axios.get(API_CALL_STRING).then((res) => {
      setMatches(matches => [res.data, ...matches])
    }).catch((err) => {
      console.error(err)
    })
  }

  console.log(latestMatchIds);
  console.log("latest 20 matches", matches)

  return (
    <div className='App'>
      <Searchbar 
        searchText={searchText}
        setSearchText={setSearchText}
        server={server}
        setServer={setServer}
        searchForLatestMatches={searchForLatestMatches}
      />
      {JSON.stringify(playerData) !== '{}' && (
        <motion.div>
          <ResultsBox playerData={playerData} />
        </motion.div>

      )}
    </div>
  );
}

export default App;
