import React from 'react'

import './ResultsBox.scss'

const ResultsBox = ({ playerData, matches }) => {

  return (
    <div className='results-wrapper'>
        <img 
            className='summoner-icon' 
            src={`http://ddragon.leagueoflegends.com/cdn/12.20.1/img/profileicon/${playerData.profileIconId}.png`} 
            alt='summoner-icon'
        />
        <h2 className='summoner-name'>{playerData.name}</h2>
        <p className='summoner-level'>Level: {playerData.summonerLevel}</p>

          {matches.length !== 0 ? matches.map((match, index) => {

            const participantIndex = match.metadata.participants.indexOf(playerData.puuid)
            const participant = match.info.participants[participantIndex]
            const participantTeam = participant.teamId

            console.log(participantTeam)

            return(
              <>
                <h5>Game {index + 1}</h5>
                <p>KDA: {participant.kills}/{participant.deaths}/{participant.assists}</p>
                <p></p>
              </>

              // [{a:1, b:2},{a:3, b:4}]

            )
          }) : <h2>no info</h2>}
  </div>
  )
}

export default ResultsBox
