import React, { useState } from 'react'
import { motion } from 'framer-motion'
import axios from 'axios'

import './App.scss';
import Searchbar from './components/Searchbar/Searchbar';
import ResultsBox from './components/ResultsBox/ResultsBox';



function App() {

  const [searchText, setSearchText] = useState('')
  const [server, setServer] = useState('eun1')

  const [playerData, setPlayerData] = useState({})
  const [matches, setMatches] = useState([])


  const searchForLatestMatches = (e) => {
    e.preventDefault()

    axios.get('http://localhost:4000/searchPlayer', { params: { username: searchText } })
      .then(res => {
        setPlayerData(res.data)
      }).catch(err => {
        console.log(err);
      })

    axios.get('http://localhost:4000/latest20Matches', { params: { username: searchText } })
      .then(res => {
        setMatches(res.data)
      }).catch(err => {
        console.log(err);
      })   
  }


  // console.log(playerData);
  // console.log(matches)

  return (
    <div className='App'>
      <Searchbar 
        searchText={searchText}
        setSearchText={setSearchText}
        server={server}
        setServer={setServer}
        searchForLatestMatches={searchForLatestMatches}
      />
      {JSON.stringify(playerData) !== '{}' && (
        <motion.div>
          <ResultsBox playerData={playerData} matches={matches} />
        </motion.div>

      )}
    </div>
  );
}

export default App;
