import React, { useEffect, useState } from 'react'
import { motion } from 'framer-motion'
import axios from 'axios'

import './App.scss';
import Searchbar from './components/Searchbar/Searchbar';
import ResultsBox from './components/ResultsBox/ResultsBox';
import regionsToContinentsMap from './assets/regions-continents-map';



function App() {

  const [searchText, setSearchText] = useState('')
  const [summonerName, setSummonerName] = useState('')
  const [selectedRegion, setSelectedRegion] = useState('EUNE')

  const [playerData, setPlayerData] = useState({})
  const [matches, setMatches] = useState([])

  const [isLoading, setIsLoading] = useState(false)




  // console.log(playerData);
  // console.log(matches)

  console.log(regionsToContinentsMap.get(selectedRegion));

  useEffect(() => {
      axios.get('http://localhost:4000/searchPlayer', { params: { username: summonerName } })
      .then(res => {
        setPlayerData(res.data)
      }).catch(err => {
        console.log(err);
      })

      axios.get('http://localhost:4000/latest20Matches', { params: { username: summonerName } })
      .then(res => {
        setMatches(res.data)
        setIsLoading(false)
      }).catch(err => {
        console.log(err);
      })   


  }, [summonerName])

  return (
    <div className='App'>
      <Searchbar 
        searchText={searchText}
        setSearchText={setSearchText}
        summonerName={summonerName}
        setSummonerName={setSummonerName}
        selectedRegion={selectedRegion}
        setSelectedRegion={setSelectedRegion}
        setIsLoading={setIsLoading}
      />
      {
        summonerName !== '' && (
        <motion.div>
          <ResultsBox playerData={playerData} matches={matches} isLoading={isLoading}/>
        </motion.div>

      )}
    </div>
  );
}

export default App;
