import React, { useState } from 'react'
import './Dropdown.scss'

const Dropdown = ({ selected, setSelected}) => {

    const [isActive, setIsActive] = useState(false)

    const options = ['NA', 'EUNE', 'TR']

  return (
    <div className='dropdown'>
        <div className='dropdown-btn' onClick={e => setIsActive(!isActive)}>Choose</div>
            {isActive && (
                <div className='dropdown-content'>
                    <div onClick={e => setSelected(e.target.textContent)} className='dropdown-item'>NA</div>
                    <div className='dropdown-item'>EUNE</div>
                    <div className='dropdown-item'>TR</div>          
                </div>
            )}
    </div>
  )
}

export default Dropdown
